<?phpnamespace App\Http\Controllers;use Illuminate\Http\Request;use App\Keyword;class DeleteController extends Controller{	public static function guide(Request $req, $model) {		return call_user_func_array("self::{$model}Delete", [$req->get('deleteArray')]);	}	public static function keywordDelete($deleteArray) {		foreach ($deleteArray as $deleteKey) {			Keyword::where('name', (string)$deleteKey)->delete();		}		return response()->json(			[				'status'    =>  'OK',				'count'     =>  count($deleteArray)			]		);	}}