<?phpnamespace App\Http\Controllers;use Illuminate\Support\Facades\Redis;class SpiderController extends Controller{	protected static $redis;	private static $SpiderAppetite = 100;	private static $totalInsects = 1000;	public static function guide($model) {		self::$redis = Redis::connection();		return call_user_func("self::{$model}");	}	public static function start() {		// 启动标记		self::busy();	}	public static function stop() {		self::spare();	}	public static function status() {		self::$redis = Redis::connection();		return self::$redis->get('spiderShouldWork');	}	protected  static function busy() {		self::$redis = Redis::connection();		return self::$redis->set('spiderShouldWork', 1);	}	protected  static function spare() {		self::$redis = Redis::connection();		return self::$redis->set('spiderShouldWork', 0);	}}