<?phpnamespace App\Http\Controllers;use App\Jobs\SpiderMainProcess;use Illuminate\Support\Facades\Redis;class SpiderController extends Controller{	protected static $redis;	public static function guide($model) {		return call_user_func("self::{$model}");	}	public static function start() {		self::busy();		dispatch(new SpiderMainProcess());	}	public static function stop() {		self::spare();	}	public static function status() {		self::$redis = Redis::connection();		return self::$redis->get('spiderShouldWork');	}	protected  static function busy() {		self::$redis = Redis::connection();		return self::$redis->set('spiderShouldWork', true);	}	protected  static function spare() {		self::$redis = Redis::connection();		return self::$redis->set('spiderShouldWork', false);	}}