<?phpnamespace App\Http\Controllers;class UploadController extends Controller{	public function handleFile() {		$uploadDir = '/uploadBin';		$uploadFile = $uploadDir . basename($_FILES['userfile']['name']);		if (move_uploaded_file($_FILES['userfile']['tmp_name'], $uploadFile)) {			echo "File is valid, and was successfully uploaded.\n";		} else {			echo "Possible file upload attack!\n";		}	}}