<?phpnamespace App\Lib;class Proxy {	static private $tid = 555565268379784;	static private $num = 1;	static private $foreign = 'none';	static private $longlife = 20;	static private $delay = 1;	static private $ips=[];	public static function fresh() {		$url = 'http://qsrdk.daili666api.com/ip/?tid=' . self::$tid			. '&num=' . self::$num			. '&foreign=' . self::$foreign			. '&longlife=' . self::$longlife			. '&delay=' . self::$delay;		self::$ips= preg_split('/\n|\r\n?/', file_get_contents($url));	}	public static function getIP(){		if (empty(self::$ips)) {			self::fresh();		}		return self::$ips[0];	}}