<?phpnamespace App\Lib;use PHPHtmlParser\Dom;class Spider {	public static function getKeywordRank($keyword){		$url = "http://www.baidu.com/s?wd={$keyword}";		$dom = new Dom();		$dom->load($url);		$result = $dom->find('h3 > a');		$test = [];		foreach ($result as $res) {			$link = $res->getAttribute('href');			$test[] = Url::get_final_url($link);		}		dd($test);	}}